import React, { useState } from "react";
import GetValue from "./components/GetValue";
import Layout from "./components/Layout";
import { apiEndPoint } from "./config";

const App = () => {
  const [artist, setArtist] = useState([]);

  const getDetails = async fieldValue => {
    // const response = await fetch(
    //   apiEndPoint + "/artists/" + this.state.fieldValue + "?app_id=app_id"
    // );
    // const myData = await response.json();
    // console.log(myData);
    const url = `${apiEndPoint}/artists/${fieldValue}/events?app_id=app_id`;
    const response = await fetch(url);
    const myData = await response.json();
    console.log(myData);
    setArtist(myData);
  };

  return (
    <React.Fragment>
      <GetValue getDetails={getDetails} />
      <Layout artist={artist} />
    </React.Fragment>
  );
};

export default App;
