import React from "react";
import Navbar from "./components/Navbar";
import GetValue from "./components/GetValue";
import Layout from "./components/Layout";
import ErrorPage from "./components/ErrorPage";
import { apiEndPoint } from "./config";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      details: null,
      events: [],
      isNotFound: false
    };
  }

  getDetails = async fieldValue => {
    const response = await fetch(
      apiEndPoint + "/artists/" + fieldValue + "?app_id=app_id"
    )
      .then(stream => stream.json())
      .then(data => data);

    console.log(response);
    this.setState({
      details: response
    });

    const eventResponse = await fetch(
      apiEndPoint + "/artists/" + fieldValue + "/events?app_id=app_id"
    );
    const myEvents = await eventResponse.json();

    // const errored = myData === "";

    // console.log(eventResponse);
    // this.setState({
    //   details: errored ? null : myData,
    //   events: errored ? [] : myEvents,
    //   isNotFound: errored
    // });
  };

  render() {
    console.log(this.state.details);
    // console.log(this.state.events);
    return (
      <React.Fragment>
        <Navbar />
        <GetValue onFetch={this.getDetails} />
        {this.state.details ? (
          <Layout
            artistInfo={this.state.details}
            artistEvents={this.state.events}
          />
        ) : (
          this.state.isNotFound && <ErrorPage />
        )}
      </React.Fragment>
    );
  }
}

export default App;
