import React from "react";
import Navbar from "./components/Navbar";
import GetValue from "./components/GetValue";
import Layout from "./components/Layout";
import ErrorPage from "./components/ErrorPage";
import { apiEndPoint } from "./config";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      details: null,
      events: []
    };
  }

  getDetails = async fieldValue => {
    const artistDetails = await fetch(
      apiEndPoint + "/artists/" + fieldValue + "?app_id=app_id"
    );

    const details = await artistDetails.json();

    const artistEvents = await fetch(
      apiEndPoint + "/artists/" + fieldValue + "/events?app_id=app_id"
    );
    const events = await artistEvents.json();

    this.setState({
      details,
      events
    });
  };

  render() {
    // console.log(this.state.details);
    // console.log(this.state.events);
    return (
      <React.Fragment>
        <Navbar />
        <GetValue onFetch={this.getDetails} />
        {this.state.details && (
          <Layout
            artistInfo={this.state.details}
            artistEvents={this.state.events}
          />
        )}
        {this.state.details === "" && <ErrorPage />}
      </React.Fragment>
    );
  }
}

export default App;
