import React from "react";
import { apiEndPoint } from "../config";

class GetValue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fieldValue: ""
    };
  }

  getDetails = async () => {
    console.log(this.state.fieldValue);
    console.log(apiEndPoint);
    const { fieldValue } = this.state;
    // const response = await fetch(
    //   apiEndPoint + "/artists/" + this.state.fieldValue + "?app_id=app_id"
    // );
    // const myData = await response.json();
    // console.log(myData);
    const url = `${apiEndPoint}/artists/${fieldValue}/events?app_id=app_id_new`;
    const response = await fetch(url, {
      method: "GET",
      headers: { "Content-Type": "application/json" }
    })
      .then(res => console.log(res))
      .catch(console.error);
    // const myData = await response.json();
    // console.log(myData);
  };

  handleChange = event => {
    this.setState({
      fieldValue: event.target.value
    });
  };

  render() {
    return (
      <div>
        <form>
          <input
            className="form-conrol"
            type="text"
            value={this.state.fieldValue}
            onChange={this.handleChange}
          ></input>
          <button className="btn btn-primary" onClick={this.getDetails}>
            Get Detail
          </button>
        </form>
      </div>
    );
  }
}

export default GetValue;
