import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fieldValue: ""
    };
  }

  getDetails = () => {
    console.log(this.state.fieldValue);
  };

  handleChange = event => {
    this.setState({
      fieldValue: event.target.value
    });
  };

  render() {
    return (
      <div>
        <input
          type="text"
          value={this.state.fieldValue}
          onChange={this.handleChange}
        ></input>
        <button onClick={this.getDetails}>Get Detail</button>
      </div>
    );
  }
}

export default App;
