import React from "react";
import GetValue from "./components/GetValue";
import Layout from "./components/Layout";
import { apiEndPoint } from "./config";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {},
      events: []
    };
  }

  getDetails = async fieldValue => {
    const response = await fetch(
      apiEndPoint + "/artists/" + fieldValue + "?app_id=app_id"
    );
    const myData = await response.json();
    artistInfo.details = myData;
    console.log(myData);
    // const url = `${apiEndPoint}/artists/${fieldValue}/events?app_id=app_id`;
    // const response = await fetch(url);
    // const myData = await response.json();
    // console.log(myData);
  };

  render() {
    console.log(this.state.details);

    return (
      <React.Fragment>
        <GetValue onFetch={this.getDetails} />
        <Layout />
      </React.Fragment>
    );
  }
}

export default App;
