import React from "react";

class GetValue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fieldValue: ""
    };
  }

  handleFetch = () => {
    const { onFetch } = this.props;
    onFetch(this.state.fieldValue);
  };

  handleChange = event => {
    this.setState({
      fieldValue: event.target.value
    });
  };

  handleKeyDown = e => {
    if (e.key === "Enter") {
      const { onFetch } = this.props;
      onFetch(this.state.fieldValue);
    }
  };

  render() {
    return (
      <div className="container mb-5">
        <div className="row justify-content-center">
          <div className="col-6">
            <div className="card p-4 border-sm d-flex align-items-center flex-row justify-content-center">
              <input
                className="form-conrol py-2 border-0 rounded
                "
                type="text"
                placeholder="Enter the artist's name"
                autoFocus
                value={this.state.fieldValue}
                onChange={this.handleChange}
                onKeyDown={this.handleKeyDown}
              ></input>
              <button className="btn bg-green" onClick={this.handleFetch}>
                <img
                  src="https://icon-library.net/images/search-icon-svg/search-icon-svg-27.jpg"
                  alt="search-icon"
                  className="search-icon"
                ></img>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default GetValue;
