import React from "react";
import { apiEndPoint } from "../config";

class GetValue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fieldValue: ""
    };
  }

  getDetails = async () => {
    console.log(this.state.fieldValue);
    console.log(apiEndPoint);
    const { fieldValue } = this.state;
    const response = await fetch(
      "https://rest.bandsintown.com/artists/JoeSatriani?app_id=app_id"
    );
    const myData = await response.json();
    console.log(myData);
    // const url = `${apiEndPoint}/artists/${fieldValue}/events?app_id=app_id`;
    // const response = await fetch(url);
    // const myData = await response.json();
    // console.log(myData);
  };

  handleChange = event => {
    this.setState({
      fieldValue: event.target.value
    });
  };

  render() {
    return (
      <div>
        <nav className="navbar navbar-light bg-light mb-4">
          <a className="navbar-brand">BandsInTown</a>
          <form>
            <input
              className="form-conrol"
              type="text"
              value={this.state.fieldValue}
              onChange={this.handleChange}
            ></input>
            <button className="btn btn-primary" onClick={this.getDetails}>
              Get Detail
            </button>
          </form>
        </nav>
      </div>
    );
  }
}

export default GetValue;
