import React from "react";

class GetValue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fieldValue: ""
    };
  }

  getDetails = async () => {
    console.log(this.state.fieldValue);

    // const response = await fetch(
    //   apiEndPoint + "/artists/" + this.state.fieldValue + "?app_id=app_id"
    // );
    // const myData = await response.json();
    // console.log(myData);

    const response = await fetch(
      apiEndPoint +
        "/artists/" +
        this.state.fieldValue +
        "/events?app_id=app_id"
    );
    const myData = await response.json();
    console.log(myData);
  };

  handleChange = event => {
    this.setState({
      fieldValue: event.target.value
    });
  };

  render() {
    return (
      <div>
        <input
          type="text"
          value={this.state.fieldValue}
          onChange={this.handleChange}
        ></input>
        <button onClick={this.getDetails}>Get Detail</button>
      </div>
    );
  }
}

export default GetValue;
