import React from "react";

class GetValue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fieldValue: ""
    };
  }

  handleFetch = () => {
    const { onFetch } = this.props;
    onFetch(this.state.fieldValue);
  };

  handleChange = event => {
    this.setState({
      fieldValue: event.target.value
    });
  };

  render() {
    return (
      <div className="container mb-5">
        <div className="row justify-content-end">
          <div className="col-6">
            <div className="card p-5 shadow">
              <label>Enter the artist's name</label>
              <input
                className="form-conrol w-100 mb-4 py-2 border rounded
                "
                type="text"
                value={this.state.fieldValue}
                onChange={this.handleChange}
              ></input>
              <button className="btn btn-primary" onClick={this.handleFetch}>
                Get Details
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default GetValue;
