import React from "react";
import GetValue from "./components/GetValue";
import Layout from "./components/Layout";
import { apiEndPoint } from "./config";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {},
      events: []
    };
  }

  getDetails = async fieldValue => {
    const response = await fetch(
      apiEndPoint + "/artists/" + fieldValue + "?app_id=app_id"
    );
    const myData = await response.json();
    this.setState({
      details: myData
    });

    const eventResponse = `${apiEndPoint}/artists/${fieldValue}/events?app_id=app_id`;
    const myEvents = await eventResponse.json();
    this.setState({
      details: myEvents
    });
  };

  render() {
    console.log(this.state.details);

    return (
      <React.Fragment>
        <GetValue onFetch={this.getDetails} />
        <Layout
          artistInfo={this.state.details}
          artistEvents={this.state.events}
        />
      </React.Fragment>
    );
  }
}

export default App;
